import React, { createContext, useContext, useState, useEffect } from 'react';
import axios from 'axios';
import { toast } from 'react-toastify';
import { useAuth } from './AuthContext';
import { useNotifications } from './NotificationContext';

const TaskContext = createContext();

// Task reducer
const taskReducer = (state, action) => {
  switch (action.type) {
    case 'LOADING_START':
      return {
        ...state,
        loading: true,
        error: null
      };
    case 'LOADING_END':
      return {
        ...state,
        loading: false
      };
    case 'SET_TASKS':
      return {
        ...state,
        tasks: action.payload,
        loading: false,
        error: null
      };
    case 'ADD_TASK':
      return {
        ...state,
        tasks: [...state.tasks, action.payload],
        loading: false,
        error: null
      };
    case 'UPDATE_TASK':
      return {
        ...state,
        tasks: state.tasks.map(task =>
          task._id === action.payload._id ? action.payload : task
        ),
        loading: false,
        error: null
      };
    case 'DELETE_TASK':
      return {
        ...state,
        tasks: state.tasks.filter(task => task._id !== action.payload),
        loading: false,
        error: null
      };
    case 'SET_CALENDAR_TASKS':
      return {
        ...state,
        calendarTasks: action.payload,
        loading: false,
        error: null
      };
    case 'SET_ERROR':
      return {
        ...state,
        error: action.payload,
        loading: false
      };
    case 'CLEAR_ERROR':
      return {
        ...state,
        error: null
      };
    default:
      return state;
  }
};

// Initial state
const initialState = {
  tasks: [],
  calendarTasks: {},
  loading: false,
  error: null
};

// TaskProvider component
export const TaskProvider = ({ children }) => {
  const [state, dispatch] = useState(initialState);
  const { token, API_BASE_URL } = useAuth();
  const { createNotification } = useNotifications();

  // Fetch all tasks
  const fetchTasks = async (filters = {}) => {
    dispatch({ type: 'LOADING_START' });
    try {
      const params = new URLSearchParams(filters).toString();
      const res = await axios.get(`${API_BASE_URL}/api/tasks?${params}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      dispatch({
        type: 'SET_TASKS',
        payload: res.data.data
      });
      return { success: true, data: res.data.data };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to fetch tasks';
      dispatch({
        type: 'SET_ERROR',
        payload: message
      });
      toast.error(message);
      return { success: false, error: message };
    }
  };

  // Fetch calendar tasks
  const fetchCalendarTasks = async (month, year) => {
    dispatch({ type: 'LOADING_START' });
    try {
      const res = await axios.get(`${API_BASE_URL}/api/tasks/calendar?month=${month}&year=${year}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      dispatch({
        type: 'SET_CALENDAR_TASKS',
        payload: res.data.data
      });
      return { success: true, data: res.data.data };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to fetch calendar tasks';
      dispatch({
        type: 'SET_ERROR',
        payload: message
      });
      toast.error(message);
      return { success: false, error: message };
    }
  };

  // Create task
  const createTask = async (taskData) => {
    dispatch({ type: 'LOADING_START' });
    try {
      const res = await axios.post(`${API_BASE_URL}/api/tasks`, taskData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      const newTask = res.data.data;
      dispatch({
        type: 'ADD_TASK',
        payload: newTask
      });
      toast.success('Task created successfully!');

      // If created task is already overdue, create a notification
      if (new Date(newTask.dueDate) < new Date() && newTask.status !== 'completed') {
        await createNotification({
          title: 'Task Overdue',
          message: `Your task "${newTask.title}" was created in an overdue state.`,
          type: 'task_overdue',
          data: { taskId: newTask._id }
        });
      }

      return { success: true, data: newTask };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to create task';
      dispatch({
        type: 'SET_ERROR',
        payload: message
      });
      toast.error(message);
      return { success: false, error: message };
    }
  };

  // Update task
  const updateTask = async (taskId, taskData) => {
    dispatch({ type: 'LOADING_START' });
    try {
      const res = await axios.put(`${API_BASE_URL}/api/tasks/${taskId}`, taskData, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      const updatedTask = res.data.data;
      dispatch({
        type: 'UPDATE_TASK',
        payload: updatedTask
      });
      toast.success('Task updated successfully!');

      // If updated task is now overdue, create a notification
      if (new Date(updatedTask.dueDate) < new Date() && updatedTask.status !== 'completed') {
        // You might want to add more logic here to avoid sending repeated notifications
        // For now, it will send a notification every time an overdue task is updated.
        await createNotification({
          title: 'Task Overdue',
          message: `Your task "${updatedTask.title}" is currently overdue.`,
          type: 'task_overdue',
          data: { taskId: updatedTask._id }
        });
      }

      return { success: true, data: updatedTask };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to update task';
      dispatch({
        type: 'SET_ERROR',
        payload: message
      });
      toast.error(message);
      return { success: false, error: message };
    }
  };

  // Delete task
  const deleteTask = async (taskId) => {
    dispatch({ type: 'LOADING_START' });
    try {
      await axios.delete(`${API_BASE_URL}/api/tasks/${taskId}`, {
        headers: {
          Authorization: `Bearer ${token}`
        }
      });
      dispatch({
        type: 'DELETE_TASK',
        payload: taskId
      });
      toast.success('Task deleted successfully!');
      return { success: true };
    } catch (error) {
      const message = error.response?.data?.message || 'Failed to delete task';
      dispatch({
        type: 'SET_ERROR',
        payload: message
      });
      toast.error(message);
      return { success: false, error: message };
    }
  };

  // Clear errors
  const clearError = () => {
    dispatch({ type: 'CLEAR_ERROR' });
  };

  const value = {
    ...state,
    fetchTasks,
    fetchCalendarTasks,
    createTask,
    updateTask,
    deleteTask,
    clearError
  };

  return (
    <TaskContext.Provider value={value}>
      {children}
    </TaskContext.Provider>
  );
};

// Custom hook to use task context
export const useTask = () => {
  const context = useContext(TaskContext);
  if (!context) {
    throw new Error('useTask must be used within a TaskProvider');
  }
  return context;
};

export default TaskContext;
